import React from 'react';

import PageHeader from '../../components/PageHeader';
import Input from '../../components/Input';

import './styles.css'

function TeacherForm() {
    return(
        <div id="page-teacher-form" className="container">
            <PageHeader title="Olá, Proffy! Que incrível que você quer dar aulas!" description="O primeiro passo é preencher este formulário de inscrição"/>
            <main>
                <fieldset>
                    <legend>
                        Seus Dados
                    </legend>
                    <Input placeholder="Nome" id="name"/>
                    <Input placeholder="Avatar" id="avatar"/>
                    <Input placeholder="Nome" id="name"/>
                    <div className="input-block">
                        <input placeholder="Avatar (link para foto, comece com http)" type="text" id="subject"/>
                    </div>
                    <div className="input-block">
                        <input placeholder="WhatsApp" type="text" id="subject"/>
                    </div>
                </fieldset>
            </main>
        </div>
    );
}

export default TeacherForm;
