import React, {InputHTMLAttributes} from 'react'

import './styles.css'
import { prependOnceListener } from 'process';

interface Properties extends InputHTMLAttributes<HTMLInputElement> {
    placeholder: string;
    id: string;
}

const Input: React.FC<Properties> = ({ placeholder, id, ...rest }) => {
    return(
        <div className="input-block">
            {
                placeholder && rest.type === "time" && 
                <label htmlFor={id}>
                    {placeholder}
                </label>
            }
            <input placeholder={placeholder} id={id} {...rest}/>
        </div>
    );
}

export default Input;
