import React, { useState, useEffect } from 'react';

//Folha de estilo
import './styles.css'

//Componentes
import PageHeader from '../../components/PageHeader';
import Teacher from '../../components/Teacher';
import Input from '../../components/Input';
import Select from '../../components/Select';
import api from '../../services/api';

function TeacherList() {

    const [subject, setSubject] = useState('');
    const [week_day, setWeekDay] = useState('');
    const [time, setTime] = useState('');

    useEffect(() => {
        api.get('classes', {
            params: {
                subject,
                week_day,
                time,
            }
        }).then()

    }, [week_day, subject, time]);

    return(
        <div id="page-teacher-list">
            <PageHeader title="Estes são os Proffys disponíveis!">
                <form id="search-teachers">
                    <Input name="Disciplina" id="subject" placeholder="Ex. Matemática"
                        value={subject}
                        onChange={(e) => { setSubject(e.target.value) }}
                    />
                    <Select 
                        name="Dia da Semana"
                        id="week_day"
                        value={week_day}
                        onChange={(e) => { setWeekDay(e.target.value) }}
                        options={[
                            {value: '0', label: "Domingo"},
                            {value: '1', label: "Segunda-Feira"},
                            {value: '2', label: "Terça-Feira"},
                            {value: '3', label: "Quarta-Feira"},
                            {value: '4', label: "Quinta-Feira"},
                            {value: '5', label: "Sexta-Feira"},
                            {value: '6', label: "Sábado"},
                        ]}
                    />
                    <Input name="Hora" id="time" type="time" 
                        value={time}
                        onChange={(e) => { setTime(e.target.value) }}
                    />
                </form>
            </PageHeader>
            <main>
                <Teacher 
                    name="Ariel Alves"
                    pic="https://thispersondoesnotexist.com/image"
                    price="90,00"
                    subject="Matemática"
                    description="Doutorado em Teoria dos Números. Leciona Àlgebra ao Ensino Médio e Teoria dos Números em Universidades"
                />
                <Teacher 
                    name="Taylor Johnson"
                    pic="https://thispersondoesnotexist.com/image"
                    price="60,00"
                    subject="Informática"
                    description="Mestrado em Educação Informacional. Leciona informática em escolas."
                />
            </main>
        </div>
    );
}

export default TeacherList;
