import React, { useState } from 'react';

//Componentes
import PageHeader from '../../components/PageHeader';
import Input from '../../components/Input';
import TextArea from '../../components/TextArea';
import Select from '../../components/Select';

//Imagens
import warning from '../../assets/images/icons/warning.svg'

//Folha de estilo
import './styles.css'

interface ScheduleItem {
    week_day: string;
    from: string;
    to: string;
}

function TeacherForm() {

    const [schedules, setSchedules] = useState([]);

    function newScheduleItem() {

    }

    return(
        <div id="page-teacher-form" className="container">
            <PageHeader title="Olá, Proffy! Que incrível que você quer dar aulas!" description="O primeiro passo é preencher este formulário de inscrição"/>
            <main>
                <fieldset>
                    <legend>
                        Seus Dados
                    </legend>
                    <Input name="Nome" placeholder="Ex. Ariel Alves" id="name"/>
                    <Input name="Avatar" placeholder="Ex. https://meuavatar.com/ariel_alves" id="avatar"/>
                    <Input name="Whatsapp" placeholder="Ex. 83988883333" id="whatsapp"/>
                    <TextArea name="Bio" placeholder="Ex. Professora Doutora em Matemática, apaixonada por números, cálculos e ensinar!" id="bio"/>
                </fieldset>
                <fieldset>
                    <legend>
                        Sobre a aula
                    </legend>
                    <Input name="Disciplina" placeholder="Ex. Matemática" id="subject"/>
                    <Input name="Hora/Aula" placeholder="Ex. 90.50" id="cost"/>
                </fieldset>
                <fieldset className="schedule-item">
                    <legend>
                        Horários disponíveis
                        <button type="button">+ Novo Horário</button>
                    </legend>
                    {
                        schedules.map(schedule => {
                            return (
                                <Select
                                    name="Dia da Semana"
                                    id="week_day"
                                    options={[
                                        {value: 0, label: "Domingo"},
                                        {value: 1, label: "Segunda-Feira"},
                                        {value: 2, label: "Terça-Feira"},
                                        {value: 3, label: "Quarta-Feira"},
                                        {value: 4, label: "Quinta-Feira"},
                                        {value: 5, label: "Sexta-Feira"},
                                        {value: 6, label: "Sábado"},
                                    ]}
                                />
                            )
                        })
                    }
                    <Input name="Das" type="time" id="from"/>
                    <Input name="Até" type="time" id="to"/>
                </fieldset>
                <footer>
                    <p>
                        <img src={warning} alt="Atenção"/>
                        Importante! <br />
                        Preencha todos os dados.
                    </p>
                    <button>Finalizar Cadastro</button>
                </footer>
            </main>
        </div>
    );
}

export default TeacherForm;
